Page._content:
if (document.body.clientWidth > 992) {
    function getBasicInfo() {
        /* Á™óÂè£È´òÂ∫¶ */
        var ViewH = $(window).height();
        /* documentÈ´òÂ∫¶ */
        var DocH = $("body")[0].scrollHeight;
        /* ÊªöÂä®ÁöÑÈ´òÂ∫¶ */
        var ScrollTop = $(window).scrollTop();
        /* ÂèØÊªöÂä®ÁöÑÈ´òÂ∫¶ */
        var S_V = DocH - ViewH;
        var Band_H = ScrollTop / (DocH - ViewH) * 100;
        return {
            ViewH: ViewH,
            DocH: DocH,
            ScrollTop: ScrollTop,
            Band_H: Band_H,
            S_V: S_V
        }
    };
    function show(basicInfo) {
        if (basicInfo.ScrollTop > 0.001) {
            $(".neko").css('display', 'block');
        } else {
            $(".neko").css('display', 'none');
        }
    }
    (function ($) {
        $.fn.nekoScroll = function (option) {
            var defaultSetting = {
                top: '0',
                scroWidth: 6 + 'px',
                z_index: 9999,
                zoom: 0.9,
                borderRadius: 5 + 'px',
                right: 60 + 'px',
                // ËøôÈáåÂèØ‰ª•Êç¢‰∏∫‰Ω†ÂñúÊ¨¢ÁöÑÂõæÁâáÔºå‰æãÂ¶ÇÊàëÂ∞±Êç¢‰∏∫‰∫ÜÈõ™‰∫∫Ôºå‰ΩÜÊòØË¶ÅÊä†Âõæ
                nekoImg: "https://s2.loli.net/2023/06/19/8fKHwovEJAL3apR.png",
                hoverMsg: "ËøîÂõûÈ°∂Á´Øüë®üèª‚ÄçüöÄ",
                color: "#6f42c1",
                during: 500,
                blog_body: "body",
            };
            var setting = $.extend(defaultSetting, option);
            var getThis = this.prop("className") !== "" ? "." + this.prop("className") : this.prop("id") !== "" ? "#" +
                this.prop("id") : this.prop("nodeName");
            if ($(".neko").length == 0) {
                this.after("<div class=\"neko\" id=" + setting.nekoname + " data-msg=\"" + setting.hoverMsg + "\"></div>");
            }
            let basicInfo = getBasicInfo();
            $(getThis)
                .css({
                    'position': 'fixed',
                    'width': setting.scroWidth,
                    'top': setting.top,
                    'height': basicInfo.Band_H * setting.zoom * basicInfo.ViewH * 0.01 + 'px',
                    'z-index': setting.z_index,
                    'background-color': setting.bgcolor,
                    "border-radius": setting.borderRadius,
                    'right': setting.right,
                    'background-image': 'url(' + setting.scImg + ')',
                    'background-image': '-webkit-linear-gradient(45deg, rgba(255, 255, 255, 0.1) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.1) 50%, rgba(255, 255, 255, 0.1) 75%, transparent 75%, transparent)', 'border-radius': '2em',
                    'background-size': 'contain'
                });
            $("#" + setting.nekoname)
                .css({
                    'position': 'fixed',
                    'top': basicInfo.Band_H * setting.zoom * basicInfo.ViewH * 0.01 - 50 + 'px',
                    'z-index': setting.z_index * 10,
                    'right': setting.right,
                    'background-image': 'url(' + setting.nekoImg + ')',
                });
            show(getBasicInfo());
            $(window)
                .scroll(function () {
                    let basicInfo = getBasicInfo();
                    show(basicInfo);
                    $(getThis)
                        .css({
                            'position': 'fixed',
                            'width': setting.scroWidth,
                            'top': setting.top,
                            'height': basicInfo.Band_H * setting.zoom * basicInfo.ViewH * 0.01 + 'px',
                            'z-index': setting.z_index,
                            'background-color': setting.bgcolor,
                            "border-radius": setting.borderRadius,
                            'right': setting.right,
                            'background-image': 'url(' + setting.scImg + ')',
                            'background-image': '-webkit-linear-gradient(45deg, rgba(255, 255, 255, 0.1) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.1) 50%, rgba(255, 255, 255, 0.1) 75%, transparent 75%, transparent)', 'border-radius': '2em',
                            'background-size': 'contain'
                        });
                    $("#" + setting.nekoname)
                        .css({
                            'position': 'fixed',
                            'top': basicInfo.Band_H * setting.zoom * basicInfo.ViewH * 0.01 - 50 + 'px',
                            'z-index': setting.z_index * 10,
                            'right': setting.right,
                            'background-image': 'url(' + setting.nekoImg + ')',
                        });
                    if (basicInfo.ScrollTop == basicInfo.S_V) {
                        $("#" + setting.nekoname)
                            .addClass("showMsg")
                    } else {
                        $("#" + setting.nekoname)
                            .removeClass("showMsg");
                        $("#" + setting.nekoname)
                            .attr("data-msg", setting.hoverMsg);
                    }
                });
            this.click(function (e) {
                btf.scrollToDest(0, 500)
            });
            $("#" + setting.nekoname)
                .click(function () {
                    btf.scrollToDest(0, 500)
                });
            return this;
        }
    })(jQuery);

    $(document).ready(function () {
        //ÈÉ®ÂàÜËá™ÂÆö‰πâ
        $("#myscoll").nekoScroll({
            bgcolor: 'rgb(0 0 0 / .5)', //ËÉåÊôØÈ¢úËâ≤ÔºåÊ≤°ÊúâÁª≥Â≠êËÉåÊôØÂõæÁâáÊó∂ÊúâÊïà
            borderRadius: '2em',
            zoom: 0.9,
            scroWidth:'3px'
        }
        );
        //Ëá™ÂÆö‰πâÔºàÂéªÊéâ‰ª•‰∏ãÊ≥®ÈáäÔºåÂπ∂Ê≥®ÈáäÊéâÂÖ∂‰ªñÁöÑÊü•ÁúãÊïàÊûúÔºâ
        /*
        $("#myscoll").nekoScroll({
            nekoname:'neko1', //nekonameÔºåÁõ∏ÂΩì‰∫éid
            nekoImg:'img/Áå´Âí™.png', //nekoÁöÑËÉåÊôØÂõæÁâá
            scImg:"img/Áª≥1.png", //Áª≥Â≠êÁöÑËÉåÊôØÂõæÁâá
            bgcolor:'#1e90ff', //ËÉåÊôØÈ¢úËâ≤ÔºåÊ≤°ÊúâÁª≥Â≠êËÉåÊôØÂõæÁâáÊó∂ÊúâÊïà
            zoom:0.9, //Áª≥Â≠êÈïøÂ∫¶ÁöÑÁº©ÊîæÂÄº
            hoverMsg:'‰Ω†Â•Ω~Âñµ', //Èº†Ê†áÊµÆÂä®Âà∞neko‰∏äÊñπÁöÑÂØπËØùÊ°Ü‰ø°ÊÅØ
            right:'100px', //Ë∑ùÁ¶ªÈ°µÈù¢Âè≥ËæπÁöÑË∑ùÁ¶ª
            fontFamily:'Ê•∑‰Ωì', //ÂØπËØùÊ°ÜÂ≠ó‰Ωì
            fontSize:'14px', //ÂØπËØùÊ°ÜÂ≠ó‰ΩìÁöÑÂ§ßÂ∞è
            color:'#1e90ff', //ÂØπËØùÊ°ÜÂ≠ó‰ΩìÈ¢úËâ≤
            scroWidth:'8px', //Áª≥Â≠êÁöÑÂÆΩÂ∫¶
            z_index:100, //‰∏çÁî®Ëß£Èáä‰∫ÜÂêß
            during:1200, //‰ªéÈ°∂ÈÉ®Âà∞Â∫ïÈÉ®ÊªëÂä®ÁöÑÊó∂Èïø
        });
        */
    })
}

