window.onscroll = percent;// ÊâßË°åÂáΩÊï∞
// È°µÈù¢ÁôæÂàÜÊØî
function percent() {
    let a = document.documentElement.scrollTop || window.pageYOffset, // Âç∑ÂéªÈ´òÂ∫¶
        b = Math.max(document.body.scrollHeight, document.documentElement.scrollHeight, document.body.offsetHeight, document.documentElement.offsetHeight, document.body.clientHeight, document.documentElement.clientHeight) - document.documentElement.clientHeight, // Êï¥‰∏™ÁΩëÈ°µÈ´òÂ∫¶
        result = Math.round(a / b * 100), // ËÆ°ÁÆóÁôæÂàÜÊØî
        up = document.querySelector("#go-up") // Ëé∑ÂèñÊåâÈíÆ

    if (result <= 95) {
        up.childNodes[0].style.display = 'none'
        up.childNodes[1].style.display = 'block'
        up.childNodes[1].innerHTML = result;
    } else {
        up.childNodes[1].style.display = 'none'
        up.childNodes[0].style.display = 'block'
    }
}

